import { useState, useEffect } from "react";

function StudentList() {
    const [students, setStudents] = useState([]);
    const [editingId, setEditingId] = useState(null);
    const [editForm, setEditForm] = useState({ name: "", age: "", course: "" });
    
    useEffect(() => {
    fetch("http://localhost:4000/api/students")
      .then(response => response.json())
      .then(data => setStudents(data.students))
      .catch(error => console.error("Error fetching students:", error));
  }, []);


function startEdit(student) {
    setEditingId(student._id);
    setEditForm({
      name: student.name,
      age: student.age,
      course: student.course
    });
}

function cancelEdit() {
    setEditingId(null);
    setEditForm({ name: "", age: "", course: "" });
}

function saveEdit(id) {
    fetch(`http://localhost:4000/api/students/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(editForm),
    })
    .then(response => response.json())
    .then(() => {
      // Refresh the student list
      return fetch("http://localhost:4000/api/students");
    })
    .then(response => response.json())
    .then(data => {
      setStudents(data.students);
      setEditingId(null);
      setEditForm({ name: "", age: "", course: "" });
    })
    .catch(error => console.error("Error updating student:", error));
}

function deleteStudent(id) {
    console.log("Deleting student with id:", id);
    fetch(`http://localhost:4000/api/students/${id}`, {
      method: "DELETE",
    })
    .then(() => {
      // After deleting, fetch the updated list of students
      return fetch("http://localhost:4000/api/students");
    })
    .then(response => response.json())
    .then(data => setStudents(data.students))
    .catch(error => console.error("Error deleting student:", error));
}

    return (
        <div style={{ padding: "20px" }}>
      <h1>Student List</h1>

      <table border="1" cellPadding="10" cellSpacing="0">
        <thead>
          <tr>
            <th>Name</th>
            <th>Age</th>
            <th>Course</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {students.map(student => (
            <tr key={student._id}>
              {editingId === student._id ? (
                // Edit mode
                <>
                  <td>
                    <input
                      type="text"
                      value={editForm.name}
                      onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={editForm.age}
                      onChange={(e) => setEditForm({ ...editForm, age: e.target.value })}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={editForm.course}
                      onChange={(e) => setEditForm({ ...editForm, course: e.target.value })}
                    />
                  </td>
                  <td>
                    <button
                      title="Save"
                      onClick={() => saveEdit(student._id)}
                      style={{ marginRight: "10px", backgroundColor: "#4CAF50", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}
                    >
                      ‚úì Save
                    </button>
                    <button
                      title="Cancel"
                      onClick={cancelEdit}
                      style={{ backgroundColor: "#f44336", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}
                    >
                      ‚úó Cancel
                    </button>
                  </td>
                </>
              ) : (
                // View mode
                <>
                  <td>{student.name}</td>
                  <td>{student.age}</td>
                  <td>{student.course}</td>
                  <td>
                    <button
                      title="Edit"
                      onClick={() => startEdit(student)}
                      style={{ marginRight: "10px" }}
                    >
                      ‚úèÔ∏è
                    </button>

                    <button
                      title="Delete"
                      onClick={() => deleteStudent(student._id)}
                    >
                      üóë
                    </button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    );
}

export default StudentList;
